    private void cbMatematicaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbMatematicaActionPerformed
        int a, b;
        a = Integer.parseInt(lblPrimerNumero.getText());
        b = Integer.parseInt(lblSegundoNumero.getText());

        if (cbMatematica.getSelectedItem().equals("Suma")){
            lblResultado.setText("" + (a + b));

        }else if (cbMatematica.getSelectedItem().equals("Resta")){
            lblResultado.setText("" + (a - b));

        }else if (cbMatematica.getSelectedItem().equals("Multiplicacion")){
            lblResultado.setText("" + (a * b));

        }else if (cbMatematica.getSelectedItem().equals("Division")){
            lblResultado.setText("" + (a / b));
        }
    }//GEN-LAST:event_cbMatematicaActionPerformed

